import { classExpression } from "@babel/types";
import { AppBar, Toolbar, IconButton, Badge, MenuItem, Menu, Typography } from "@material-ui/core";
import { LocalMall } from "@material-ui/icons";
import React, { useEffect, useState } from "react";
import Cart from "../Cart/cart";
import useStyles from './style';
import {createStore} from 'redux';
import rootReducer from "../../Reducers/rootReducer";


const store = createStore(rootReducer);



function handleUpdateData(){
    const data = [];
  fetch('https://backendapi.turing.com/products?page=1&limit=20')
  .then(res => res.json())
  .then(data => data )
  const actionADD = {items : data}
  store.dispatch(data)
}


const Navbar = () => {
    useEffect(() => handleUpdateData(), [] )
    const classes = useStyles();
    const [isItemCart, setIsItemCart] = useState(false);

    return ( 
        <div>
            <AppBar className={classes.appBar}>
                <Toolbar>
                    <Typography  className={classes.title} >SHOPMATE</Typography>
                    <IconButton  aria-label="Show car items" color="inherit"  onClick={() => setIsItemCart(true)}>
                        <Badge badgeContent={3} color="secondary">
                            <LocalMall />
                        </Badge>
                    </IconButton>
                </Toolbar>
            </AppBar>
            {isItemCart && <Cart/>}
        </div>
     );
}
 
export default Navbar;
