import { makeStyles } from '@material-ui/core';
const useStyles = makeStyles(() =>({
    isHover:{
        //padding: '16.2% 8.5%',
        top:'0',
        left:'0',
        height:'100%',
        width:'100%',
        objectFit:'cover',
        position:'absolute',
        
        alignItems:'center',
       
    },
    root :{
        maxWidth : '100%',
        paddingTop: '25%',
        paddingBottom: '25%',
        '&:hover': { 
            backgroundColor : 'blue',
            opacity: '0.7',
            backgroundColor :"white",
            boxShadow :'5px 5px 10px 5px rgba(0, 0, 0, .2)',
           }
    },
    media :{
        height:0,
        paddingTop:'56.25%',
        margin : '10%',
    },
    CardContent:{
        fontWeight :'bolder',
        fontFamily: "Lato",
    },

}))
export default useStyles;
