import { makeStyles } from '@material-ui/core';
export default makeStyles(() =>({
    root :{
        maxWidth : '100%',
        color:"blue",
        '&:hover': { 
            backgroundColor : 'blue',
           }
    },
    media :{
        height:0,
        paddingTop:'56.25%',
    },
    CardContent:{
        display:'flex',
        justifyContent :'space-between',
    }

}))
