import React, { useEffect, useState } from "react";
import { Grid, Paper } from "@material-ui/core";
import useStyles from "./style";
import Item from "../item/item";

const Cart = () => {
    const [Cart, setCart] = useState([]);
    useEffect(() => {
        fetch('https://backendapi.turing.com/shoppingcart/5')
        .then(res => res.json())
        .then(data => setCart(data))
    }, [] )
    const classes = useStyles();
    return (
        <div className={classes.popupView}>
            <div className={classes.popupCard}>
                <a><i className="fas fa-times"></i></a>
                <div className={classes.product}>
                <Grid container justify="center" spacing={3}>
                    {Cart.map((item) => (
                    <Grid item key={item.product_id} xs={12} sm={12} md={12} lg={12}>
                    <Item product_id={item.product_id} name={item.name} price ={item.price} thumbnail={item.thumbnail} />
                </Grid>
            ))}
        </Grid> 
                </div>
            </div>
        </div>
     );
}
 
export default Cart;
