import React, { JSXElementConstructor, useState } from "react";
import {Card, Grid, Typography, Container, CardHeader, CardMedia, CardContent, CardActions, Button } from '@material-ui/core';
import useStyles from './style';


interface Props {
    product_id : number;
    name : string;
    price: number;
    thumbnail : string;
}

const [isHovering, setIsHovering] = useState(false);
function handleMouseEnter(){
    setIsHovering(true); 
}

const Item = ({product_id, name, price, thumbnail}:Props) => {
    const classes = useStyles();
    return (
        <div>
        <Container className="isHover" hidden> 
            <Button color="primary">Buy Now</Button>
        </Container>
        <Card className={classes.root} >
            <Typography className= {classes.CardContent} variant="h5" gutterBottom align ="center"> {name}</Typography>
            <CardMedia className ={classes.media} image={"https://backendapi.turing.com/images/products/"+thumbnail} title ={name}/>
            <Typography className= {classes.CardContent} variant="h5" color ='secondary' gutterBottom align ="center">{price}</Typography>

        </Card>
        </div>
      );
    }
export default Item;
