import { makeStyles } from '@material-ui/core';
const useStyles = makeStyles(() =>({
    root :{
        maxWidth : '100%',
        paddingTop: '30px',
        paddingBottom: '30px',
        // '&:hover': { 
        //     backgroundColor : 'blue',
        //    }
    },
    media :{
        height:0,
        paddingTop:'56.25%',
        margin : '10%',
    },
    CardContent:{
        display:'flex',
        justifyContent :'space-between',
    },

}))
export default useStyles;
