import { makeStyles } from '@material-ui/core';
const useStyles = makeStyles(() =>({
    isHover:{
        padding: '16.2% 8.5%',
        position:'absolute',
       
    },
    root :{
        position:'relative',
        maxWidth : '100%',
        paddingTop: '25%',
        paddingBottom: '25%',
        '&:hover': { 
            backgroundColor : 'blue',
            //opacity: '0.2',
            backgroundColor :"white",
            boxShadow :'5px 5px 10px 5px rgba(0, 0, 0, .2)',
           }
    },
    media :{
        height:0,
        paddingTop:'56.25%',
        margin : '10%',
    },
    CardContent:{
        fontWeight :'bolder',
        fontFamily: "Lato",
    },

}))
export default useStyles;
