import React, { useEffect, useState } from "react";
import { Button, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from "@material-ui/core";
import useStyles from "./style";
import DeleteIcon from '@material-ui/icons/Delete';

const Cart = () => {
    const [Cart, setCart] = useState([]);
    useEffect(() => {
        fetch('https://backendapi.turing.com/shoppingcart/5')
        .then(res => res.json())
        .then(data => setCart(data))
    }, [] )
    const classes = useStyles();
    return (
        <div className={classes.popupView}>
            <div className={classes.popupCard}>
                <div className={classes.product}>
                                <TableContainer component={Paper}>
                    <Table className ={classes.tablePreview} aria-label="simple table">
                        <TableHead>
                        <TableRow>
                            <TableCell>item</TableCell>
                            <TableCell align="right">Size</TableCell>
                            <TableCell align="right">Qunatity</TableCell>
                            <TableCell align="right">Price</TableCell>
                        </TableRow>
                        </TableHead>
                        <TableBody>
                        {Cart.map((row) => (
                            <TableRow key={row.item_id}>
                            <TableCell className={classes.itemDetails} component="th" scope="row">
                                <img src={"https://backendapi.turing.com/images/products/"+row.image} width="100px" height='100px'  alt={row.attributes} />
                                <div className={classes.itemName}>
                                <Typography variant="h5">{row.name}</Typography>
                                <Typography variant="h6">{row.attributes}</Typography>
                                <Button variant="outlined"  color="secondary"  startIcon={<DeleteIcon/>}> Remove </Button>
                                </div>
                            </TableCell>
                            <TableCell align="right">{row.subtotal}</TableCell>
                            <TableCell align="right">{row.quantity}</TableCell>
                            <TableCell align="right">{row.price}</TableCell>
                            </TableRow>
                        ))}
                        </TableBody>
                    </Table>
                    </TableContainer>
                </div>
            </div>
        </div>
     );
}
 
export default Cart;
