import { makeStyles } from '@material-ui/core';
const useStyles = makeStyles(() =>({
    isHover:{

        position: 'absolute',
        marginTop:'30%',
        opacity:'none',
       
    },
    root :{
        maxWidth : '100%',
        paddingTop: '25%',
        paddingBottom: '25%',
        position:'absolute',
        '&:hover': { 
            opacity: '0.2',
            boxShadow :'5px 5px 10px 5px rgba(0, 0, 0, .2)',
           }
    },
    media :{
        height:0,
        paddingTop:'56.25%',
        margin : '10%',
        
    },
    CardContent:{
        fontWeight :'bolder',
        fontFamily: "Lato",
    },

}))
export default useStyles;
