        gridArea: 'inner-div',
       
    },
    root :{
        maxWidth : '100%',
        paddingTop: '25%',
        paddingBottom: '25%',
        display: 'grid',
        placeItems: 'center',
        gridTemplateAreas:'inner-div',
        '&:hover': { 
            backgroundColor : 'blue',
            opacity: '0.2',
            backgroundColor :"white",
            boxShadow :'5px 5px 10px 5px rgba(0, 0, 0, .2)',
           }
    },
    media :{
        height:0,
        paddingTop:'56.25%',
        margin : '10%',
    },
    CardContent:{
        fontWeight :'bolder',
        fontFamily: "Lato",
    },

}))
