import React, { useEffect, useState } from "react";
import { Grid, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from "@material-ui/core";
import useStyles from "./style";
import Item from "../item/item";

const Cart = () => {
    const [Cart, setCart] = useState([]);
    useEffect(() => {
        fetch('https://backendapi.turing.com/shoppingcart/5')
        .then(res => res.json())
        .then(data => setCart(data))
    }, [] )
    const classes = useStyles();
    return (
        <div className={classes.popupView}>
            <div className={classes.popupCard}>
                <a><i className="fas fa-times"></i></a>
                <div className={classes.product}>
                                <TableContainer component={Paper}>
                    <Table  aria-label="simple table">
                        <TableHead>
                        <TableRow>
                            <TableCell>item</TableCell>
                            <TableCell align="right">Size</TableCell>
                            <TableCell align="right">Qunatity</TableCell>
                            <TableCell align="right">Price</TableCell>
                        </TableRow>
                        </TableHead>
                        <TableBody>
                        {Cart.map((row) => (
                            <TableRow key={row.item_id}>
                            <TableCell component="th" scope="row">{
                                <img src={"https://backendapi.turing.com/images/products/"+row.image} alt="" />
                            }</TableCell>
                            <TableCell align="right">{row.subtotal}</TableCell>
                            <TableCell align="right">{row.quantity}</TableCell>
                            <TableCell align="right">{row.price}</TableCell>
                            </TableRow>
                        ))}
                        </TableBody>
                    </Table>
                    </TableContainer>
                </div>
            </div>
        </div>
     );
}
 
export default Cart;
