import React, { JSXElementConstructor } from "react";
import {Card, Grid, Typography, CardHeader, CardMedia, CardContent, CardActions, Button } from '@material-ui/core';
import useStyles from './style';


interface Props {
    product_id : number;
    name : string;
    price: number;
    thumbnail : string;
}


const Item = ({product_id, name, price, thumbnail}:Props) => {
    const classes = useStyles();
    return (
        <Card className={classes.root}>
            <Typography variant="h4" gutterBottom align ="center"> {name}</Typography>
            <CardMedia className ={classes.media} image={"https://backendapi.turing.com/images/products/"+thumbnail} title ={name}/>
            <Typography variant="h4" gutterBottom align ="center">{price}</Typography>

        </Card>
      );
    }
export default Item;
