import React, { useState,useEffect, Component } from "react";
import {Grid, Typography } from '@material-ui/core';
import Item from './item/item';
import { connect } from 'react-redux'


class ItemPage extends React.Component<any, any> {
    public render( ) {
        const {items} = this.props;
        
        return (
            <Grid container justify="center" spacing={3}>
            {(items).map((item) => (
                <Grid item key={item.product_id} xs={12} sm={6} md={4} lg={3}>
                    <Item product_id={item.product_id} name={item.name} price ={item.price} thumbnail={item.thumbnail} />
                </Grid>
            ))}
        </Grid> 
        );
    }
    
}


// const [items, setItems] = useState([]);
// useEffect(() => {
//     fetch('https://backendapi.turing.com/products?page=1&limit=20')
//     .then(res => res.json())
//     .then(data => setItems(data.rows))
// }, [] )



 const mapStateToProps = (state) => {
     return {
         items : state.items
     }
 }
export default connect(mapStateToProps)(ItemPage);
